import React, { useContext, useEffect } from 'react';
import { MyContext } from './Contenxt.api';
import { useNavigate } from 'react-router-dom';

function Redirect() {
    const { state, setState } = useContext(MyContext);
    const navigate = useNavigate();

    useEffect(() => {
        const storedAuthorization = localStorage.getItem('authorization');
        if (storedAuthorization) {
            setState({ authorization: storedAuthorization });
        } else {
            navigate('/login');
        }
    }, [setState, navigate]);

    // Kullanıcı oturum bilgilerini saklamak için state değişikliklerini localStorage'a yazma
    useEffect(() => {
        localStorage.setItem('authorization', state.authorization);
    }, [state.authorization]);

