import React from 'react';
import { BrowserRouter as Router, Route, Routes } from 'react-router-dom';
import GlobalStyle from "./globalStyle";
import Components from "./components";
import Layout from './pages/Layout';
import Login from "./pages/Login";
import Redirect from "./pages/Redirect"; // Yeni eklendi
import { MyProvider } from "./pages/Contenxt.api"; // MyProvider burada çağrılmalı
import { MyContext } from "./pages/Contenxt.api"; // MyContext burada çağrılmalı

function App() {
    return (
        <MyProvider>
            <MyContext.Consumer>
                {({ state }) => (
                    <div className="App">
                        <GlobalStyle />
                        <Router>
                            <Routes>
                                <Route
                                    exact
                                    path="/"
                                    element={state.authorization ? <Components /> : <Redirect to="/login" />}
                                />
                                <Route exact path="/404" component={NotFoundImage} />
                                {/* ... Diğer rotalar ... */}
                            </Routes>
                        </Router>
                    </div>
                )}
            </MyContext.Consumer>
        </MyProvider>
    );
}

export default App;
